mapper.select:
SELECT * FROM notice
WHERE or notice_subject like concat('%',#{searchKeyword},'%') or notice_content like concat('%',#{searchKeyword},'%')
ORDER BY notice_idx DESC LIMIT #{startRow} , #{listLimit}

mapper.insert:
INSERT INTO notice
VALUES (
	null
	, #{notice_subject}
	, #{notice_content}
	, now()
)

mapper.select:
SELECT * FROM faq
WHERE FAQ_category like concat('%',#{searchKeyword},'%') or FAQ_subject like concat('%',#{searchKeyword},'%') or FAQ_content like concat('%',#{searchKeyword},'%')
ORDER BY FAQ_idx DESC LIMIT #{startRow} , #{listLimit}

mapper.insert:
INSERT INTO faq
VALUES (
	null
	, #{FAQ_category}
	, #{FAQ_subject}
	, #{FAQ_content}
	, now()
)

mapper.update:
UPDATE faq
SET 
	FAQ_category = #{FAQ_category}
	,FAQ_subject = #{FAQ_subject}
	,FAQ_content = #{FAQ_content}
WHERE
	FAQ_idx = #{FAQ_idx}

mapper.select:
SELECT * FROM cs
WHERE cs_category like concat('%',#{searchKeyword},'%') or cs_subject like concat('%',#{searchKeyword},'%') or cs_content like concat('%',#{searchKeyword},'%') or cs_consultant_name like concat('%',#{searchKeyword},'%')
ORDER BY cs_idx DESC LIMIT #{startRow} , #{listLimit}

mapper.update:
UPDATE cs
SET
	cs_check = "확인"
	, cs_answer = #{cs_answer}
	, cs_consultant_name = #{cs_consultant_name}
WHERE 
	cs_idx = #{cs_idx}
